css:
.PortalStatusBarIndicator.PortalStatusBarIndicator.PortalStatusBarIndicator:before {
  margin-right: 0;
}
.PortalStatusBarIndicator.transmitting {
  color: #6494ed;
}
.PortalStatusBarIndicator.transmitting:hover {
  color: #6494ed;
}
.PortalStatusBarIndicator.outdated {
  color: #ff6347;
}
.PortalStatusBarIndicator.outdated:hover {
  color: #ff6347;
}
.TeletypePopoverTooltip {
  user-select: none;
}
.TeletypePopoverTooltip.tooltip {
  width: 315px;
  padding-left: 10px;
  padding-right: 10px;
  box-sizing: border-box;
}
.TeletypePopoverTooltip .tooltip-inner.tooltip-inner.tooltip-inner.tooltip-inner {
  padding: 10px;
  background-color: #21252b;
  border: 1px solid #181a1f;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  color: #9da5b4;
  max-height: 300px;
  white-space: normal;
  overflow-y: auto;
}
.TeletypePopoverTooltip.top .tooltip-arrow.tooltip-arrow {
  width: 10px;
  height: 10px;
  border-width: 0 0 1px 1px;
  border-color: #181a1f;
  background-color: #21252b;
  border-bottom-right-radius: 2px;
  transform: rotate(-45deg);
}
.TeletypePopoverComponent {
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: -10px;
  color: #9da5b4 !important;
}
.PortalListComponent {
  flex: 1;
  display: flex;
  flex-direction: column;
  height: 100%;
}
.PortalListComponent--initializing {
  text-align: center;
}
.PortalParticipants {
  flex: auto;
  display: flex;
  flex-direction: row;
  align-items: center;
}
.PortalParticipants-guests {
  flex: auto;
  display: flex;
  flex-direction: row;
  align-items: center;
  align-content: start;
  flex-wrap: wrap;
}
.PortalParticipants-participant {
  margin: 5px;
}
.PortalParticipants-participant img {
  height: 22px;
  border-radius: 50%;
}
.PortalParticipants-site-1 {
  margin-left: 0;
}
.PortalParticipants-site-1 img {
  height: 28px;
}
.HostPortalComponent {
  flex: none;
}
.HostPortalComponent-status {
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 0 10px;
}
.HostPortalComponent-share-toggle {
  flex: none;
}
.HostPortalComponent-share-toggle label {
  margin: 0;
  font-size: 80%;
}
.HostPortalComponent-connection-info,
.HostPortalComponent-GuestPortalsContainer {
  border-bottom: 1px solid #181a1f;
  padding: 0 10px;
}
.HostPortalComponent-connection-info {
  display: flex;
  flex-direction: column;
  align-items: stretch;
  padding: 10px 10px 5px;
  position: relative;
}
.HostPortalComponent-connection-info-heading.creating-portal {
  visibility: hidden;
}
.HostPortalComponent-connection-info-heading h1 {
  margin-top: 0;
  margin-bottom: 5px;
  font-weight: normal;
  font-size: 80%;
}
.HostPortalComponent-connection-info-heading h1::before {
  line-height: 1em;
  margin-right: 5px;
}
.HostPortalComponent-connection-info-portal-id {
  display: flex;
  flex-direction: row;
  margin-bottom: 5px;
}
.HostPortalComponent-connection-info-portal-id.creating-portal {
  visibility: hidden;
}
.HostPortalComponent-connection-info-portal-id .input-text {
  display: flex;
  flex: auto;
}
.HostPortalComponent-connection-info-portal-id .btn {
  margin-left: 5px;
  flex: none;
}
.HostPortalComponent-connection-info-spinner {
  position: absolute !important;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
}
.HostPortalComponent .host-id-input {
  font-family: Menlo, Consolas, 'DejaVu Sans Mono', monospace;
  font-size: 80%;
  color: #9da5b4;
}
.PortalListComponent-GuestPortalsContainer {
  flex: 1;
}
.GuestPortalComponent {
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 0 10px;
}
.GuestPortalComponent-leave.btn {
  flex: none;
  width: 50px;
}
.GuestPortalComponent-leave.btn label {
  margin: 0;
  font-size: 80%;
}
.SignInComponent {
  text-align: center;
  padding: 10px;
}
.SignInComponent atom-text-editor {
  font-size: 12px !important;
  margin-bottom: 10px;
  text-align: left;
}
.SignInComponent-GitHubLogo::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 40px;
  width: 40px;
  height: 40px;
  content: "\f00a";
}
.JoinPortalComponent--no-prompt,
.JoinPortalComponent--prompt {
  border-top: 1px solid #181a1f;
}
.JoinPortalComponent--no-prompt {
  flex: none;
  height: 40px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
}
.JoinPortalComponent--no-prompt label {
  color: rgba(157, 165, 180, 0.6);
  font-size: 12px;
  margin: 0;
}
.JoinPortalComponent--no-prompt label:hover {
  color: white;
  cursor: pointer;
}
.JoinPortalComponent--prompt {
  flex: none;
  height: 40px;
  text-align: left;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  padding: 0 10px;
}
.JoinPortalComponent--prompt atom-text-editor {
  flex: auto;
  font-size: 80% !important;
}
.JoinPortalComponent--prompt .btn {
  flex: none;
  width: 50px;
  margin-left: 5px;
}
.cursors.blink-off .cursor.non-blinking {
  opacity: 1;
}
.cursor.ParticipantCursor--site-1 {
  border-left-color: #0088ff;
}
.cursor.ParticipantCursor--site-2 {
  border-left-color: #20b684;
}
.cursor.ParticipantCursor--site-3 {
  border-left-color: #cc8533;
}
.cursor.ParticipantCursor--site-4 {
  border-left-color: #D831B0;
}
.cursor.ParticipantCursor--site-5 {
  border-left-color: #EBDD5B;
}
.cursor.ParticipantCursor--site-6 {
  border-left-color: #001eff;
}
.cursor.ParticipantCursor--site-7 {
  border-left-color: #20aab6;
}
.cursor.ParticipantCursor--site-8 {
  border-left-color: #ccc433;
}
.cursor.ParticipantCursor--site-9 {
  border-left-color: #d8316a;
}
.cursor.ParticipantCursor--site-10 {
  border-left-color: #bdeb5b;
}
.cursor.ParticipantCursor--site-11 {
  border-left-color: #8c00ff;
}
.cursor.ParticipantCursor--site-12 {
  border-left-color: #2046b6;
}
.cursor.ParticipantCursor--site-13 {
  border-left-color: #6ecc33;
}
.cursor.ParticipantCursor--site-14 {
  border-left-color: #d86731;
}
.cursor.ParticipantCursor--site-15 {
  border-left-color: #5deb5b;
}
.SitePositionsComponent {
  display: flex;
  flex-direction: column;
  position: absolute;
  right: 15px;
  text-align: right;
}
.SitePositionsComponent img {
  cursor: pointer;
  height: 40px;
  border-radius: 50%;
}
.SitePositionsComponent .SitePositionsComponent-site {
  display: flex;
}
.SitePositionsComponent.upper-right {
  top: 15px;
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
}
.SitePositionsComponent.upper-right .SitePositionsComponent-site {
  margin-left: 5px;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
}
.SitePositionsComponent.middle-right {
  top: 50%;
  transform: translateY(-50%);
  display: flex;
  flex-direction: column;
  align-items: flex-end;
}
.SitePositionsComponent.middle-right .SitePositionsComponent-site {
  margin-bottom: 5px;
  flex-direction: row;
  align-items: center;
}
.SitePositionsComponent.lower-right {
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  bottom: 15px;
}
.SitePositionsComponent.lower-right .SitePositionsComponent-site {
  margin-left: 5px;
  flex-direction: column;
  justify-content: flex-end;
  align-items: center;
}
.PackageOutdatedComponent {
  color: #9da5b4;
  text-align: center;
  padding: 0 10px 10px 10px;
}
