css:
/*
* modified from rgbkrk/atom-script - styles/script.less
* https://github.com/rgbkrk/atom-script
* https://atom.io/packages/script
*/
.run-options-view .panel-heading {
  text-align: center;
  font-size: 1.25rem;
}
.run-options-view table {
  width: 100%;
  margin-top: 5px;
}
.run-options-view table td {
  padding: 5px;
  vertical-align: middle;
}
.run-options-view table td atom-text-editor {
  width: 99%;
}
.run-options-view table td:first-child {
  width: 25%;
  text-align: right;
}
.run-options-view .btn-group {
  padding: 0.5rem;
  display: flex;
}
.run-options-view .btn-group * {
  flex: 1;
  padding: 0.2rem;
}
